static final BaseAPI _api = Injector()<BaseAPI>(); 

  /// Gets the account's status. 
  /// 
  /// For parameters information refer to [GetAccountStatusRequest]. 
  /// Throws an [BaseAPIException] if API response contains an error. 
  static Future<GetAccountStatusReceive> fetchAccountStatusRaw({String? loginId,}) async { 
    final GetAccountStatusReceive response = await _api.call( 
      request: GetAccountStatusRequest(loginid: loginId), 
    ); 

    checkException( 
      response: response, 
      exceptionCreator: ({BaseExceptionModel? baseExceptionModel}) => 
          BaseAPIException(baseExceptionModel: baseExceptionModel), 
    ); 

    return response; 
  } 

  /// Gets the account's status. 
  /// 
  /// For parameters information refer to [GetAccountStatusRequest]. 
  /// Throws an [BaseAPIException] if API response contains an error. 
  static Future<GetAccountStatusResponse> fetchAccountStatus({String? loginId,}) async { 
    final GetAccountStatusReceive response = await fetchAccountStatusRaw(loginId: loginId); 

    return GetAccountStatusResponse.fromJson(response.getAccountStatus); 
  }

